[
{
"statement": "Identifier les éléments indispensables de la définition d'une douleur thoracique aiguë.",
"propositions": [
{
"correct": true,
"proposition": "Survenue de manière brutale",
"justification": "Elle est définie par une apparition brutale."
},
{
"correct": true,
"proposition": "Localisation dans la région de la cage thoracique (entre le cou et l'ombilic)",
"justification": "La zone concernée est la cage thoracique, bornée entre le cou et l'ombilic."
},
{
"correct": false,
"proposition": "Nécessite une intensité sévère",
"justification": "L'intensité fait partie des caractéristiques variables; aucune intensité minimale n'est exigée."
},
{
"correct": false,
"proposition": "Exige obligatoirement une irradiation",
"justification": "L'irradiation est une caractéristique variable, elle peut être présente ou absente."
},
{
"correct": true,
"proposition": "Inclut la notion de douleur ou de gêne",
"justification": "La définition accepte douleur ou gêne dans la région thoracique."
}
]
},
{
"statement": "Reconnaître les situations correspondant à une douleur thoracique aiguë.",
"propositions": [
{
"correct": true,
"proposition": "Gêne brutale située entre le cou et l'ombilic",
"justification": "Gêne et survenue brutale dans les limites anatomiques définies."
},
{
"correct": false,
"proposition": "Douleur thoracique installée progressivement",
"justification": "L'apparition doit être brutale, non progressive."
},
{
"correct": false,
"proposition": "Douleur brutale à la jambe",
"justification": "Localisation hors de la cage thoracique définie."
},
{
"correct": true,
"proposition": "Douleur brutale dans la région de la cage thoracique, calmée par un facteur soulageant",
"justification": "La présence d'un facteur soulageant n'exclut pas la définition; ces facteurs font partie des caractéristiques variables."
},
{
"correct": true,
"proposition": "Gêne thoracique brutale avec irradiation",
"justification": "L'irradiation est possible mais non obligatoire; la survenue brutale et la localisation suffisent."
}
]
},
{
"statement": "Différencier ce qui est variable de ce qui est fixé par la définition.",
"propositions": [
{
"correct": true,
"proposition": "L'intensité est une caractéristique variable",
"justification": "L'intensité fait explicitement partie des caractéristiques variables."
},
{
"correct": true,
"proposition": "La localisation (au sein de la cage thoracique) est une caractéristique variable",
"justification": "La localisation est citée parmi les caractéristiques variables."
},
{
"correct": false,
"proposition": "La survenue brutale est une caractéristique variable",
"justification": "La survenue brutale constitue un critère fixe de la définition."
},
{
"correct": true,
"proposition": "L'irradiation est une caractéristique variable",
"justification": "L'irradiation est mentionnée parmi les caractéristiques variables."
},
{
"correct": true,
"proposition": "La durée est une caractéristique variable",
"justification": "La durée est listée parmi les caractéristiques variables."
}
]
},
{
"statement": "Préciser la portée des termes et des limites anatomiques dans la définition.",
"propositions": [
{
"correct": true,
"proposition": "Le terme "gêne" est recevable autant que "douleur"",
"justification": "La définition inclut douleur ou gêne."
},
{
"correct": false,
"proposition": "Seul le terme "douleur" est admis",
"justification": "La gêne est également acceptée."
},
{
"correct": true,
"proposition": "La zone concernée s'étend entre le cou et l'ombilic",
"justification": "Les limites anatomiques sont précisées ainsi."
},
{
"correct": false,
"proposition": "La fréquence des épisodes doit être quotidienne",
"justification": "La fréquence fait partie des caractéristiques variables; aucune périodicité imposée."
},
{
"correct": true,
"proposition": "Un facteur déclenchant ou soulageant peut être présent ou absent",
"justification": "Ces facteurs sont des caractéristiques variables."
}
]
},
{
"statement": "Classer les propositions selon le respect simultané des critères essentiels (région thoracique définie et survenue brutale).",
"propositions": [
{
"correct": true,
"proposition": "Douleur brutale localisée entre le cou et l'ombilic, avec irradiation possible",
"justification": "Respecte la localisation et la survenue; l'irradiation est variable."
},
{
"correct": false,
"proposition": "Gêne thoracique d'apparition progressive",
"justification": "La progression s'oppose à la survenue brutale exigée."
},
{
"correct": false,
"proposition": "Douleur brutale sous l'ombilic",
"justification": "Localisation en dehors des limites entre cou et ombilic."
},
{
"correct": true,
"proposition": "Gêne brutale dans la région de la cage thoracique, quelle que soit l'intensité",
"justification": "La survenue est brutale et l'intensité est une caractéristique variable."
},
{
"correct": true,
"proposition": "Douleur brutale dans la région de la cage thoracique, de durée variable",
"justification": "La durée est variable et n'altère pas la définition si la survenue et la localisation sont conformes."
}
]
}
]
