\[
{
"statement": "Définir les deux grandes voies de contamination fœtale pendant la grossesse.",
"propositions": \[
{
"correct": true,
"proposition": "Voie vaginale ascendante lors du passage dans la filière génitale ou en cas de rupture prolongée des membranes.",
"justification": "Voie décrite comme ascendante, liée au passage lors de l'accouchement et à la rupture prolongée des membranes."
},
{
"correct": true,
"proposition": "Voie hématogène par passage anténatal transplacentaire.",
"justification": "La transmission peut survenir avant la naissance par franchissement transplacentaire."
},
{
"correct": false,
"proposition": "Il n'existe qu'une seule voie de contamination fœtale pendant la grossesse.",
"justification": "Deux voies sont distinguées : vaginale ascendante et hématogène transplacentaire."
},
{
"correct": false,
"proposition": "La contamination hématogène survient au moment de l'accouchement.",
"justification": "La contamination hématogène est anténatale et transplacentaire, non limitée à l'accouchement."
},
{
"correct": false,
"proposition": "La voie vaginale n'est possible qu'en l'absence de rupture des membranes.",
"justification": "La rupture prolongée des membranes fait partie des circonstances favorisant la voie vaginale ascendante."
}
]
},
{
"statement": "Classer les agents infectieux selon la voie de transmission indiquée.",
"propositions": \[
{
"correct": true,
"proposition": "Streptocoque B — voie vaginale ascendante.",
"justification": "Le streptocoque B est cité parmi les agents de la voie vaginale ascendante."
},
{
"correct": true,
"proposition": "Hépatite B — voie vaginale ascendante.",
"justification": "L'hépatite B est mentionnée avec la voie vaginale ascendante lors du passage dans la filière génitale."
},
{
"correct": true,
"proposition": "Herpès simplex virus — voie vaginale ascendante.",
"justification": "L'HSV est associé à la contamination lors du passage dans la filière génitale."
},
{
"correct": true,
"proposition": "Cytomégalovirus — voie hématogène transplacentaire.",
"justification": "Le cytomégalovirus est listé parmi les agents transmis par passage anténatal transplacentaire."
},
{
"correct": false,
"proposition": "Listéria — voie vaginale ascendante.",
"justification": "La listéria est rangée dans les agents de transmission hématogène transplacentaire."
}
]
},
{
"statement": "Identifier les éléments qui doivent faire évoquer une contamination au cours de la grossesse.",
"propositions": \[
{
"correct": true,
"proposition": "Hyperthermie/fièvre maternelle.",
"justification": "La fièvre maternelle fait partie des points d'appel cliniques."
},
{
"correct": true,
"proposition": "Altération du bien-être fœtal ou anomalies échographiques.",
"justification": "Des éléments fœtaux à l'échographie ou une altération du bien-être doivent faire évoquer une contamination."
},
{
"correct": true,
"proposition": "Résultat de dépistage sérologique nécessitant interprétation.",
"justification": "La contamination peut être suspectée lors d'un dépistage sérologique et de son interprétation."
},
{
"correct": false,
"proposition": "Seule une sérologie positive est évocatrice; les signes cliniques et fœtaux ne le sont pas.",
"justification": "Les signes cliniques maternels et fœtaux sont également cités comme éléments évocateurs."
},
{
"correct": false,
"proposition": "L'absence de fièvre suffit à exclure une contamination.",
"justification": "La fièvre n'est qu'un point d'appel parmi d'autres (signes fœtaux, sérologie) et son absence n'exclut pas."
}
]
},
{
"statement": "Reconnaître les conséquences possibles d'une contamination selon l'agent et le terme.",
"propositions": \[
{
"correct": true,
"proposition": "Menace d'accouchement prématuré.",
"justification": "Cette éventualité fait partie des conséquences possibles."
},
{
"correct": true,
"proposition": "Infection intra-utérine.",
"justification": "Elle est explicitement mentionnée parmi les complications possibles."
},
{
"correct": true,
"proposition": "Fausse couche tardive.",
"justification": "La fausse couche tardive est listée comme possible issue."
},
{
"correct": true,
"proposition": "Syndrome malformatif et/ou retard de croissance intra-utérin.",
"justification": "Ces deux conséquences sont citées comme risques potentiels."
},
{
"correct": false,
"proposition": "Absence totale de risque de mort fœtale in utero.",
"justification": "La mort fœtale in utero fait partie des issues possibles."
}
]
},
{
"statement": "Relier circonstances et temporalité aux voies de contamination.",
"propositions": \[
{
"correct": true,
"proposition": "Passage anténatal transplacentaire → voie hématogène.",
"justification": "La voie hématogène est définie par la transmission transplacentaire avant la naissance."
},
{
"correct": true,
"proposition": "Passage dans la filière génitale à l'accouchement → voie vaginale ascendante.",
"justification": "La voie vaginale ascendante survient lors du passage dans la filière génitale."
},
{
"correct": true,
"proposition": "Rupture prolongée des membranes → favorise la voie vaginale ascendante.",
"justification": "La rupture prolongée des membranes est associée à cette voie de contamination."
},
{
"correct": false,
"proposition": "La voie hématogène nécessite une rupture prolongée des membranes.",
"justification": "La voie hématogène est transplacentaire anténatale et ne dépend pas de la rupture des membranes."
},
{
"correct": false,
"proposition": "La voie vaginale se définit par une transmission transplacentaire.",
"justification": "La transmission transplacentaire caractérise la voie hématogène, pas la voie vaginale."
}
]
}
]
